    private void cookieActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cookieActionPerformed
        itemadd("Cookie \t\t 0.50");
        totalPrice += 0.50;
          totalUpdate();     
    }//GEN-LAST:event_cookieActionPerformed

    private void popActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_popActionPerformed
         itemadd("POP \t\t 1.50");
        totalPrice += 1.50;
        totalUpdate();
    }//GEN-LAST:event_popActionPerformed

