    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed
      String output ="";
        for (int i=0; i<itemslist.countItems();i++){
            output +=itemslist.getItem(i)+"\r\n";
        }
        output+= "TotalPrice: "+totalPrice;
 NewClass fileadd = new NewClass(output);
        
    }//GEN-LAST:event_saveActionPerformed

    private void clearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearActionPerformed
      totalPrice= 0.00;
      itemslist.clear();
      totalUpdate();
    }//GEN-LAST:event_clearActionPerformed
    public void totalUpdate(){
      total.setText("Total:"+totalPrice);
                    
    }
    public void itemadd(String toadd){
    itemslist.add(toadd);
    }
